INSERT INTO `wp_6_posts` VALUES (205,2,'2014-10-21 22:19:28','2014-10-21 22:19:28','<h1>The conference for passionate product people</h1>\r\n<h2>June 4 at the SFJAZZ Center, San Francisco</h2>\r\nMind the Product is THE product management conference, and it\'s finally coming to San Francisco. We bring together product leaders from around the world to explore the intersection of design, technology and business, inspiring you to build better products.\r\n\r\n<h2>Tickets</h2>\r\n<p style=\"padding: 0px;\">All 100 Early Bird Tickets sold out in just seconds, so sign up for our newsletter for a reminder of the full release on February 12!\r\n<!-- newsletter sign up --><div id=\"mc_embed_signup\"><form id=\"mc-embedded-subscribe-form\" class=\"validate\" action=\"http://mindtheproduct.us5.list-manage1.com/subscribe/post?u=9f9609492007e56236a074b27&amp;id=babd9cfe61\" method=\"post\" name=\"mc-embedded-subscribe-form\" novalidate=\"\" target=\"_blank\"><input id=\"mce-EMAIL\" class=\"email\" name=\"EMAIL\" required=\"\" type=\"email\" value=\"\" placeholder=\"email address\" /><input id=\"mc-embedded-subscribe\" class=\"button\" name=\"subscribe\" type=\"submit\" value=\"Subscribe\" /></form></div>\r\n</p>','Mind the Product','','publish','open','open','','mind-product','','','2015-01-15 01:36:28','2015-01-15 01:36:28','',0,'http://mtpcon.com/sf/?page_id=205',0,'page','',0);
